  //spcInit ()
  //  初期化
  public static void spcInit () {

    //モード
    spcSCSIEXOn = false;
    spcSCSIINOn = false;

    //インタフェイス
    spcSCSIEXChip = new SPCChip (true);  //拡張SCSI
    spcSCSIINChip = new SPCChip (false);  //内蔵SCSI

    //ユニット
    spcUnitArray = new SCUnit[16];
    for (int u = 0; u < 16; u++) {
      spcUnitArray[u] = new SCUnit (u, null);
    }

    //拡張SCSIのROM
    {
      byte[] bb = null;
      //-scsiexromで指定されたSCSIEXROMのイメージファイル(8KB)を読み込む
      if (spcSCSIEXROM.length () > 0) {  //-scsiexromが指定された
        bb = new byte[8192];
        if (XEiJ.ismLoad (bb, 0, 8192, spcSCSIEXROM)) {
          if (ByteArray.byaRls (bb, 0x0044) == ('S' << 24 | 'C' << 16 | 'S' << 8 | 'I') &&
              ByteArray.byaRwz (bb, 0x0048) == ('E' << 8 | 'X') &&  //SCSIEXフラグがある
              (ByteArray.byaRls (bb, 0x0020) & -0x2000) == 0x00ea0000) {  //ROM起動ハンドルがscsiexromを指している
          } else {
            XEiJ.prgMessage (Multilingual.mlnJapanese ?
                             XEiJ.smrSramName + " は拡張 SCSI ROM のデータではないか、壊れています" :
                             XEiJ.smrSramName + " is not Expansion SCSI ROM data or it's broken");
            spcSCSIEXROM = "";
            bb = null;
          }
        } else {
          spcSCSIEXROM = "";
          bb = null;
        }
      }
      if (bb == null) {
        bb = spcMakeSCSIROM (0);  //SCSIEXROMを作る
      }
      if (bb != null) {
        System.arraycopy (bb, 0, MainMemory.mmrM8, 0x00ea0000, 8192);  //メインメモリに転送しておく。必要なときだけマップされる
      }
    }

    //メニュー
    spcMenu = XEiJ.createMenu ("SCSI");  //横に長いとサブメニューを開きにくいので短くする

    //ローカルでないときは使用不可
    if (!XEiJ.prgIsLocal) {  //ローカルでないとき
      XEiJ.setEnabled (spcMenu, false);
      return;
    }

    //パラメータ
    spcLastFile = new File (".");  //カレントディレクトリ
    for (int u = 0; u < 16; u++) {
      SCUnit unit = spcUnitArray[u];
      String path = Settings.sgsCurrentMap.get ("sc" + u);
      boolean userWriteProtect = false;
      if (path.toUpperCase ().endsWith (":R")) {  //書き込み禁止モードで開く
        path = path.substring (0, path.length () - 2);
        userWriteProtect = true;
      }
      boolean hostWriteProtect = !new File (path).canWrite ();
      if (path.length () != 0) {
        unit.connect (true);  //接続されていなければ接続する
        if (unit.insert (path)) {  //挿入できた
          if (userWriteProtect || hostWriteProtect) {  //書き込みを禁止する
            unit.protect (false);  //開くときに書き込みを禁止した場合はイジェクトするまで書き込みを許可できない
          }
          spcLastFile = new File (path);
        }
      }
    }

    //アクションリスナー
    ActionListener listener = new ActionListener () {
      @Override public void actionPerformed (ActionEvent ae) {
        Object source = ae.getSource ();
        String command = ae.getActionCommand ();
        switch (command) {
        case "External SCSI Port":  //拡張 SCSI ポート
          spcSCSIEXRequest = ((JCheckBoxMenuItem) source).isSelected ();
          break;
        case "Create New SCSI Hard Disk Images":  //SCSI ハードディスクイメージの新規作成
          spcOpenFormatDialog ();
          break;
        }
      }
    };

    //メニュー
    XEiJ.addComponents (
      spcMenu,
      XEiJ.createHorizontalBox (Multilingual.mlnText (XEiJ.createLabel ("SCSI Hard Disk / CD-ROM"), "ja", "SCSI ハードディスク / CD-ROM")),
      XEiJ.createHorizontalSeparator ()
      );
    for (int u = 0; u < 16; u++) {
      spcMenu.add (spcUnitArray[u].getMenuBox ());
    }
    XEiJ.addComponents (
      spcMenu,
      XEiJ.createHorizontalSeparator (),
      XEiJ.mdlSCSIINMenuItem,
      spcSCSIEXMenuItem = Multilingual.mlnText (XEiJ.createCheckBoxMenuItem (spcSCSIEXRequest, "External SCSI Port", listener), "ja", "拡張 SCSI ポート"),
      XEiJ.createHorizontalSeparator (),
      Multilingual.mlnText (XEiJ.createMenuItem ("Create New SCSI Hard Disk Images", listener), "ja", "SCSI ハードディスクイメージの新規作成")
      );

    //最後にアクセスしたファイル
    spcLastFile = new File (".");  //最初にファイルチューザーを開いたときカレントディレクトリを表示する

    spcOpenUnit = 0;
    spcOpenWriteProtect = false;

    //開くダイアログ
    spcOpenDialog = null;
    spcOpenFileChooser = null;
    spcOpenFileFilter = new javax.swing.filechooser.FileFilter () {  //java.io.FileFilterと紛らわしい
      @Override public boolean accept (File file) {
        if (file.isDirectory ()) {
          return true;
        }
        String path = file.getPath ();
        if (spcIsInserted (path)) {  //既に挿入されている
          return false;
        }
        return spcIsHds (file, true) || spcIsIso (file);  //拡張子がHDSで装置初期化されているか、拡張子がISO
      }
      @Override public String getDescription () {
        return Multilingual.mlnJapanese ? "SCSI ハードディスク / CD-ROM イメージ (*.HDS,*.ISO)" : "SCSI Hard Disk / CD-ROM Image (*.HDS,*.ISO)";
      }
    };

    //フォーマットダイアログ
    spcFormatDialog = null;
    spcFormatFileChooser = null;
    spcFormatPartitionMegaBytes = 100;
    spcFormatBytesPerRecord = 512;
    spcFormatPartitioningCheckBox = null;
    spcFormatPartitioningOn = true;
    //spcFormatCopySystemFiles = true;
    spcFormatCopyHumanSysCheckBox = null;
    spcFormatCopyCommandXCheckBox = null;
    spcFormatCopyHumanSysOn = true;
    spcFormatCopyCommandXOn = true;
    spcFormatFilter = new javax.swing.filechooser.FileFilter () {  //java.io.FileFilterと紛らわしい
      @Override public boolean accept (File file) {
        if (file.isDirectory ()) {
          return true;
        }
        String path = file.getPath ();
        if (spcIsInserted (path)) {  //既に挿入されている
          return false;
        }
        return spcIsHds (file, false);  //拡張子がHDSならばファイルが存在しなくてもよい
      }
      @Override public String getDescription () {
        return Multilingual.mlnJapanese ? "SCSI ハードディスクイメージ (*.HDS)" : "SCSI Hard Disk Image (*.HDS)";
      }
    };

  }  //spcInit()

  //spcReset ()
  public static void spcReset () {
    //拡張SCSI
    spcSCSIEXOn = spcSCSIEXRequest;
    XEiJ.busSuper (spcSCSIEXOn ? MemoryMappedDevice.MMD_EXS : MemoryMappedDevice.MMD_NUL, 0x00ea0000, 0x00ea2000);  //必要なときだけ接続する
    //内蔵SCSI
    //  内蔵SASIと同じページにあるので内蔵SCSIがなくてもページごと切り離すことはできない
    spcSCSIINOn = XEiJ.mdlSCSIINRequest;
    if (XEiJ.romIPLVersion >= 3) {  //IPLROM1.3以上
      spcSCSIINOn = true;  //ROM1.3は内蔵SCSIがないと起動できないので強制的にONにする。チェックボックスはそのまま
      if (XEiJ.romROM30Data != null) {  //-rom30が指定された
        System.arraycopy (XEiJ.romROM30Data, 0, MainMemory.mmrM8, 0x00fc0000, 1024 * 128);  //-rom30で指定されたデータをコピーする
      } else {
        int d = MainMemory.mmrRls (0x00ff0020);
        for (int a = 0x00fc0000; a < 0x00fc0020; a += 4) {
          MainMemory.mmrWl (a, d);
        }
        MainMemory.mmrWl (0x00fc0020, MainMemory.mmrRls (0x00ff0024));
        MainMemory.mmrWstr (0x00fc0024, "SCSIIN");
        Arrays.fill (MainMemory.mmrM8, 0x00fc002a, 0x00fe0000, (byte) 0xff);
      }
    } else if (spcSCSIINOn) {  //IPLROM1.2以下で内蔵SCSIあり
      if (XEiJ.romSCSIINROMData != null) {  //-scsiinromが指定された
        System.arraycopy (XEiJ.romSCSIINROMData, 0, MainMemory.mmrM8, 0x00fc0000, 8192);  //-scsiinromで指定されたデータをコピーする
        Arrays.fill (MainMemory.mmrM8, 0x00fc2000, 0x00fe0000, (byte) 0xff);
      } else {
        byte[] bb = spcMakeSCSIROM (1);  //SCSIINROMを作る
        if (bb != null) {  //成功
          System.arraycopy (bb, 0, MainMemory.mmrM8, 0x00fc0000, 8192);
          Arrays.fill (MainMemory.mmrM8, 0x00fc2000, 0x00fe0000, (byte) 0xff);
        } else {  //失敗
          spcSCSIINOn = false;
          System.arraycopy (MainMemory.mmrM8, 0x00fe0000, MainMemory.mmrM8, 0x00fc0000, 1024 * 128);  //IPLROMをコピーする
        }
      }
    } else {  //IPLROM1.2以下で内蔵SCSIなし
      System.arraycopy (MainMemory.mmrM8, 0x00fe0000, MainMemory.mmrM8, 0x00fc0000, 1024 * 128);  //IPLROMをコピーする
    }
    XEiJ.romCheck2 ();  //ROMデバッガとROM Humanのバージョンを確認する
    XEiJ.romPatch2 ();  //ROMデバッガにパッチをあてる
    XEiJ.romPatch3 ();  //ROM Humanにパッチをあてる
    //    拡張  内蔵  unit0-7  unit8-15
    //    -----------------------------
    //    有効  有効    拡張     内蔵
    //    有効  無効    拡張     無効
    //    無効  有効    内蔵     無効
    //    無効  無効    無効     無効
    spcSCSIEXChip.spiReset (spcSCSIEXOn ? 0 : -8);
    spcSCSIINChip.spiReset (spcSCSIINOn ? spcSCSIEXOn ? 8 : 0 : -8);
    for (int u = 0; u < 16; u++) {
      spcUnitArray[u].scuReset (u < 8 ? spcSCSIEXOn ? spcSCSIEXChip : spcSCSIINOn ? spcSCSIINChip : null :
                                spcSCSIEXOn && spcSCSIINOn ? spcSCSIINChip : null);
    }
    if (spcSCSIEXOn) {
      MainMemory.mmrWb (0x00ed0070, MainMemory.mmrRbs (0x00ed0070) | 0x08);  //拡張フラグをセットする
    } else {
      MainMemory.mmrWb (0x00ed0070, MainMemory.mmrRbs (0x00ed0070) & ~0x08);  //拡張フラグをクリアする
    }
  }  //spcReset()

