  //spcOpenFiles (list, reset)
  //  開くダイアログで選択されたファイルを開く
  //  コマンドラインのみ
  public static void spcOpenFiles (File[] list, boolean reset) {
    for (int u = spcOpenUnit, k = 0; k < list.length; ) {
      if (u >= 16) {
        reset = false;  //ユニットが足りないときはリセットをキャンセルする
        break;
      }
      SCUnit unit = spcUnitArray[u];  //ユニット
      if (!unit.abuConnected) {  //接続されていない
        u++;
        continue;
      }
      File file = list[k++];  //イメージファイル
      if (!file.isFile ()) {  //ファイルが存在しない
        reset = false;  //ファイルが存在しないときはリセットをキャンセルする
        continue;
      }
      if (unit.insert (file.getPath ())) {  //挿入できた
        if (spcOpenWriteProtect || !file.canWrite ()) {  //書き込みを禁止する
          unit.protect (false);  //開くときに書き込みを禁止した場合はイジェクトするまで書き込みを許可できない
        }
        spcLastFile = file;  //最後にアクセスしたファイル
        u++;
      } else {
        reset = false;  //挿入できないファイルがあったときはリセットをキャンセルする
      }
    }
    if (reset) {
      if (spcOpenUnit < 8) {
        XEiJ.mpuReset (0xa000, SPC_HANDLE_EX + (spcOpenUnit << 2));  //拡張SCSIがなければ内蔵SCSIに読み替えられる
      }
    }
  }  //spcOpenFiles(File[],boolean)

