    //  SCSI割り込みベクタ番号を内蔵SCSIと拡張SCSIで振り分ける
    //    00FFCE88  706C                      moveq.l #$6C,d1                 ;SCSI割り込みベクタ番号(内蔵SCSI)
    //    00FFCE88  70F6                      moveq.l #$F6,d1                 ;SCSI割り込みベクタ番号(拡張SCSI)
    final int vectorDst = 0x00ffce88 - iocsSrc + iocsDst;
    ByteArray.byaWw (bb, vectorDst - romDst, type == 0 ? 0x70f6 : 0x706c);  //0=拡張SCSI,1=内蔵SCSI
    //  DMA転送時のMPUキャッシュ制御ルーチンの呼び出しをリロケートする
    //    00FFDBD8  6100 xxxx                 bsr.w   $00FF8284               ;DMA転送時のMPUキャッシュ制御ルーチン
    //ByteArray.byaWw (bb, 0x00ffdbd8 + 2 - iocsSrc + iocsDst - romDst, 0x00ff8284 - cacheFlushSrc + cacheFlushDst - (0x00ffdbd8 + 2 - iocsSrc + iocsDst));
    final int callCacheFlushDst = 0x00ffdbd8 - iocsSrc + iocsDst;
    ByteArray.byaWl (bb, callCacheFlushDst - romDst, 0x61000000 | 65535 & cacheFlushDst - (0x00ffdbd8 + 2 - iocsSrc + iocsDst));
    //  SCSIポートベースアドレスの参照をリロケートする
    //
    //  パッチをあてていないとき
    //    00FFCCF0  2C7A xxxx                 movea.l $00FF933C(pc),a6        ;SCSIポートベースアドレス。SCSIコールを呼び出す直前
    //  パッチをあてたとき
    //    00FFCCBC  2C7A xxxx                 movea.l $00FF933C(pc),a6        ;SCSIポートベースアドレス。SCSIコールを呼び出す直前
    //
    //    00FFCE12  2C7A xxxx                 movea.l $00FF933C(pc),a6        ;SCSIポートベースアドレス。SCSIコール$00 _S_RESET
    //    00FFCEC4  2C7A xxxx                 movea.l $00FF933C(pc),a6        ;SCSIポートベースアドレス。SCSI割り込みルーチン
    //    00FFCEDA  2C7A xxxx                 movea.l $00FF933C(pc),a6        ;SCSIポートベースアドレス。SCSIコール$02 _S_SELECTA
    //    00FFCF00  2C7A xxxx                 movea.l $00FF933C(pc),a6        ;SCSIポートベースアドレス。SCSIコール$01 _S_SELECT
    //    00FFCFCE  2C7A xxxx                 movea.l $00FF933C(pc),a6        ;SCSIポートベースアドレス。SCSIコール$03 _S_CMDOUT
    //    00FFD02A  2C7A xxxx                 movea.l $00FF933C(pc),a6        ;SCSIポートベースアドレス。SCSIコール$0C _S_DATAOUTI
    //    00FFD06A  2C7A xxxx                 movea.l $00FF933C(pc),a6        ;SCSIポートベースアドレス。SCSIコール$0B _S_DATAINI
    //    00FFD0AE  2C7A xxxx                 movea.l $00FF933C(pc),a6        ;SCSIポートベースアドレス。SCSIコール$06 _S_STSIN
    //    00FFD0EC  2C7A xxxx                 movea.l $00FF933C(pc),a6        ;SCSIポートベースアドレス。SCSIコール$07 _S_MSGIN
    //    00FFD12A  2C7A xxxx                 movea.l $00FF933C(pc),a6        ;SCSIポートベースアドレス。SCSIコール$08 _S_MSGOUT
    //    00FFD166  2C7A xxxx                 movea.l $00FF933C(pc),a6        ;SCSIポートベースアドレス。SCSIコール$0D _S_MSGOUTEXT
    //    00FFD196  2C7A xxxx                 movea.l $00FF933C(pc),a6        ;SCSIポートベースアドレス。SCSIコール$09 _S_PHASE
    //    00FFD57C  2C7A xxxx                 movea.l $00FF933C(pc),a6        ;SCSIポートベースアドレス。SCSIコール$05 _S_DATAOUT
    //    00FFD5C4  2C7A xxxx                 movea.l $00FF933C(pc),a6        ;SCSIポートベースアドレス。SCSIコール$04 _S_DATAIN
    //    a6が設定されていない場所から呼ばれないものはnopで潰してしまってもよいが念のためすべてリロケートする
    final int[] portBaseAddressArray = new int[] {
/*
      //  パッチをあてていないとき
      0x00ffccf0, 0x00ffce12, 0x00ffcec4, 0x00ffceda, 0x00ffcf00, 0x00ffcfce, 0x00ffd02a, 0x00ffd06a, 
      0x00ffd0ae, 0x00ffd0ec, 0x00ffd12a, 0x00ffd166, 0x00ffd196, 0x00ffd57c, 0x00ffd5c4,
*/
      //  パッチをあてたとき
      0x00ffccbc, 0x00ffce12, 0x00ffcec4, 0x00ffceda, 0x00ffcf00, 0x00ffcfce, 0x00ffd02a, 0x00ffd06a, 
      0x00ffd0ae, 0x00ffd0ec, 0x00ffd12a, 0x00ffd166, 0x00ffd196, 0x00ffd57c, 0x00ffd5c4,
    };
    for (int a : portBaseAddressArray) {
      final int portBaseDst = a - iocsSrc + iocsDst;
      final int portBaseHandleDst = 0x00ff933c - iplSrc + iplDst;
      ByteArray.byaWl (bb, portBaseDst - romDst, 0x2c7a0000 | 65535 & portBaseHandleDst - (portBaseDst + 2));
    }
    //  DMA転送終了時にDMA1のDARを復元するコードを追加する
    //    00FFDC6C                            rts
    //              60xx                      bra.s   dar1sasiDst
    final int jumpDar1Sasi1Dst = 0x00ffdc6c - iocsSrc + iocsDst;
    ByteArray.byaWw (bb, jumpDar1Sasi1Dst - romDst, 0x6000 | 255 & dar1sasiDst - (0x00ffdc6c + 2 - iocsSrc + iocsDst));
    //    00FFDCD8                            rts
    //              60xx                      bra.s   dar1sasiDst
    final int jumpDar1Sasi2Dst = 0x00ffdcd8 - iocsSrc + iocsDst;
    ByteArray.byaWw (bb, jumpDar1Sasi2Dst - romDst, 0x6000 | 255 & dar1sasiDst - (0x00ffdcd8 + 2 - iocsSrc + iocsDst));

    //DMA1のDARをSASIに戻す処理
    //    00000000  23FC 00E9 6001 00E8 4054  move.l  #$00E96001,$00E84054.l  ;DMA1のDARにSASIデータポートを設定する
    //    0000000A  4E75                      rts
    ByteArray.byaWwArray (bb, dar1sasiDst - romDst, new int[] {
      0x23fc, 0x00e9, 0x6001, 0x00e8, 0x4054, 0x4e75,
    });

    //新しいSCSIポート初期化ルーチン
    //  IOCS _SCSIDRVを登録する
    //    既に同じアドレスが登録されているときは初期化済みとみなして何もせずに復帰する
    //  _B_SEEK/_B_VERIFY/_B_DSKINI/_B_DRVSNS/_B_WRITE/_B_READ/_B_RECALI/_B_ASSIGN/_B_BADFMT/_B_FORMAT/_B_EJECTを差し替える
