    //scuWriteImage ()
    public void scuWriteImage () {
      int oc = scuChip.spiCommandBuffer[0] & 255;
      if (oc == 0x0a || oc == 0x2a) {  //Write(6)またはWrite(10)
        try {
          scuRaf.write (scuRecordImage, 0, scuBytesPerRecord);
        } catch (IOException ioe) {
        }
      }
    }  //scuWriteImage()

